import React, { useState } from 'react';
import axios from 'axios';
import RepositoryList from './components/RepositoryList';
import MutualFollowersList from './components/MutualFollowersList';

function App() {
  const [username, setUsername] = useState('');
  const [userData, setUserData] = useState(null);
  const [repositories, setRepositories] = useState([]);
  const [mutualFollowers, setMutualFollowers] = useState([]);

  const handleSearch = async () => {
    try {
      // Fetch user data
      const userResponse = await axios.get(`http://localhost:5000/api/users/${username}`);
      setUserData(userResponse.data);

      // Fetch repositories
      const repoResponse = await axios.get(`http://localhost:5000/api/users/repositories/${username}`);
      setRepositories(repoResponse.data);

      // Fetch mutual followers (replace "mutualUser" with actual user)
      const mutualFollowersResponse = await axios.get(`http://localhost:5000/api/users/mutual-followers/${username}/mutualUser`);
      setMutualFollowers(mutualFollowersResponse.data);

    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  return (
    <div className="App">
      <input 
        type="text" 
        value={username} 
        onChange={(e) => setUsername(e.target.value)} 
        placeholder="Enter GitHub Username" 
      />
      <button onClick={handleSearch}>Search</button>

      {userData && (
        <div>
          <h2>{userData.login}</h2>
          <p>Created At: {new Date(userData.created_at).toLocaleDateString()}</p>
          <RepositoryList repositories={repositories} />
          <MutualFollowersList mutualFollowers={mutualFollowers} />
        </div>
      )}
    </div>
  );
}

export default App;
